import AuthService from "../../services/auth.service";
import axios from "axios";
const Expense = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [allExpenses, setAllExpenses] = useState([]);
    const [budget, setBudget] = useState('');
    const [expenseGroupByCategory, setExpenseGroupByCategory] = useState({});

    useEffect(() => {
        getAllExpenses();
    },[]);

    const getAllExpenses = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        const budget = AuthService.getTotalBudget();
        setUsername(username);
        setToken(token);
        setBudget(budget);

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/expense`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllExpenses(response.data);
        }).catch(error => {
            console.log(error.message);
        }).then(
            groupAllExpenses
        )

        console.log(allExpenses);

    }


    const groupAllExpenses = () => {
        console.log(allExpenses.keys());
        let result = allExpenses?.reduce((acc,d) => {
            if(Object.keys(acc).includes(d.categoryName)) return acc;
            acc[d.categoryName] = allExpenses.filter(name => name.categoryName === d.categoryName);
            return acc;
        }, {})
        setExpenseGroupByCategory(result);
    }


    return (
        <div>
            <h4>Expenses</h4>
        </div>
    )
}

export default Expense;
