import React, {useState, useEffect} from "react";
import AuthService from "../../services/auth.service";
import axios from "axios";
import {Button, Table} from "reactstrap";
import Tab from "bootstrap/js/src/tab";
import CategoryUpdate from "./CategoryUpdate";

const Category = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [emptyCategory, setEmptyCategory] = useState({categoryName: '', categoryBudget: ''});
    const [allCategories, setAllCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState([]);
    const [isCreate, setIsCreate] = useState(true);



    useEffect(() => {
        getAllCategories();
    },[]);

    const getAllCategories = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        setUsername(username);
        setToken(token);

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/category`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllCategories(response.data);
        }).catch(error => {
            console.log(error.message);
        })
    }

    const deleteCategory = (name) => {
        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }
        const url = `/${username}/category/${name}`;

        axios.delete(url, requestHeader).then(response => {
            console.log(response.data);
            window.location.reload();
        }).catch(error => {
            console.log(error.message);
        })

    }

    const categoryRows = allCategories?.map(category => {
        return (
            <tr key={category.categoryName}>
                <td>{category.categoryName}</td>
                <td>{category.categoryBudget}</td>
                <td><Button className={""} size={"sm"}>Update Category</Button>
                    <Button className={""} size={"sm"}
                            onClick={() => deleteCategory(category.categoryName)}>Delete Category</Button>
                </td>
            </tr>
        )
    })

    return (
        <div>
            <h2>Hi {username}, Use this page to manage your categories.</h2>
            <div>
                <Button size={"lg"}>Create Category</Button>
            </div>
            <div>
                <CategoryUpdate />
            </div>
            <div>
                <Table>
                    <thead>
                    <tr>
                        <th>Category Name</th>
                        <th>Budget</th>
                        <th>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                        {categoryRows}
                    </tbody>
                </Table>
            </div>
        </div>
    )
}

export default Category;
