import React, {useState, useEffect} from "react";
import AuthService from "../../services/auth.service";
import axios from "axios";
import {Container} from "react-bootstrap";
import ExpenseCard from "./ExpenseCard";
import CategoryCard from "../Category/CategoryCard";

const Expense = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [allExpenses, setAllExpenses] = useState([]);
    const [budget, setBudget] = useState('');
    const [allCategoryNames, setAllCategoryNames] = useState([]);
    const [expenseGroupByCategory, setExpenseGroupByCategory] = useState({});

    useEffect(() => {
        getAllExpenses();
    },[]);

    const getAllExpenses = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        const budget = AuthService.getTotalBudget();
        setUsername(username);
        setToken(token);
        setBudget(budget);

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/expense`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllExpenses(response.data);
            return response.data
        }).catch(error => {
            console.log(error.message);
        }).then(response => response.reduce((acc,d) => {
            if(Object.keys(acc).includes(d.categoryName)) return acc;
            acc[d.categoryName] = response.filter(name => name.categoryName === d.categoryName);
            return acc;
        }, {})).then(result => setExpenseGroupByCategory(result));

    }

    /*then( response => {
        response.map(e => {
            const cNames = allCategoryNames;
            cNames.push(e.categoryName);
            setAllCategoryNames(cNames);
        })
    })*/

    /*const print = Object.keys(expenseGroupByCategory).forEach(obj => {
        const myArray = expenseGroupByCategory[obj];
        console.log(myArray);
        myArray.forEach(e => console.log(e.expenseId))
    })*/

    const expenseCards = () => {
        for(const[key, value] of Object.entries(expenseGroupByCategory)) {
            const catName = key;
            console.log(catName);
            const catTotalBudget = 0;
            console.log(catTotalBudget)
            const expenses = value.reduce((acc,obj) => {
                return acc + obj.amount
            }, 0);
            console.log(expenses)
            return (
                <div>
                    <p>{catName} + {catTotalBudget} + {expenses}</p>

                </div>
            )
        }
    }


    return (
        <div>
            <p>EXXX</p>
            {expenseCards()}
        </div>
       /* <Container className={"pt-4"}>
            {expenseCards}
            {/!*<div style={{display:"grid", gridTemplateColumns: "repeat(auto-fill, minmax(500px,50%))", gap: "1rem",
                alignItems: "flex-start"}}>
                {expenseCards}
            </div>*!/}
        </Container>*/
    )
}

export default Expense;
