import React, {useState, useEffect} from "react";
import AuthService from "../../services/auth.service";
import axios from "axios";
const Expense = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [allExpenses, setAllExpenses] = useState([]);
    const [budget, setBudget] = useState('');
    const [expenseGroupByCategory, setExpenseGroupByCategory] = useState({});

    useEffect(() => {
        getAllExpenses();
    },[]);

    const getAllExpenses = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        const budget = AuthService.getTotalBudget();
        setUsername(username);
        setToken(token);
        setBudget(budget);

        getExpenses();


    }

   const getExpenses = async () => {
       const requestHeader = {
           headers : {
               "Content-Type": "application/json",
               "Authorization": "Bearer " + token
           }
       }
       const url = `/${username}/expense`;

      await axios.get(url, requestHeader).then(response => {
           console.log(response.data);
           setAllExpenses(response.data);
       }).catch(error => {
           console.log(error.message);
       })

       await console.log(allExpenses);

       let result = await allExpenses.reduce((acc,d) => {
           if(Object.keys(acc).includes(d.categoryName)) return acc;
           acc[d.categoryName] = allExpenses.filter(name => name.categoryName === d.categoryName);
           return acc;
       }, {})
       setExpenseGroupByCategory(result);
   }

    return (
        <div>
            <h4>Expenses</h4>
        </div>
    )
}

export default Expense;
