import React, {useState} from "react";
import {Form, FormGroup, Label, Input} from "reactstrap";
import {useNavigate} from "react-router-dom";
import authService from "../services/auth.service";
import axios from 'axios';
import "./login.css";

//const baseURL = "http://localhost:8080/api/auth";

const Login = (props) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const required = value => {
        if (!value) {
            return (
                <div className="alert alert-danger" role="alert">
                    This field is required!
                </div>
            );
        }
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        const requestBody = {
            "username": username,
            "password": password
        }

        axios.post('/auth/signin', requestBody).then(response => {
            console.log(response.data);
            authService.setUserSession(username, response.data.jwtToken);
            props.authenticate();
            //navigate('/home');
        }).catch(error => {
           // console.log('Error: ' + error.toJSON())
            console.log(error.message);
        })
    }

    return (
        <div>
           <div className={"col-md-12"}>
               <div className={"card card-body"}>
                   <Form onSubmit={handleFormSubmit}>
                       <div className={"form-text"}>
                           <label htmlFor={"username"}>Username</label>
                           <Input type={"text"} className={"form-control"} name={"username"} value={username}
                                  onChange={event => setUsername(event.target.value)}
                           validations={[required]} />
                       </div>
                   </Form>
               </div>
               <h4>Login</h4>
               username: <input className="field" type="text" value={username} onChange={event => setUsername(event.target.value)} /> <br/>
               password: <input className="field" type="password" value={password} onChange={event => setPassword(event.target.value)} /> <br/>
           </div>
            <div>
                <input className="inner" type="button" value="Login" onClick={handleFormSubmit} />
            </div>
        </div>

    )

}



