import React, {useState, useEffect} from "react";
import AuthService from "../../services/auth.service";
import axios from "axios";
import {Button} from "reactstrap";

const Category = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [emptyCategory, setEmptyCategory] = useState({categoryName: '', categoryBudget: ''});
    const [allCategories, setAllCategories] = useState({});
    const [selectedCategory, setSelectedCategory] = useState({});

    useEffect(() => {
        getAllCategories();
    },[]);

    const getAllCategories = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        setUsername(username);
        setToken(token);

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/category`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllCategories(response.data);
        }).catch(error => {
            console.log(error.message);
        })
    }

    return (
        <div>
            <h2>Hi {username}, Use this page to manage your categories.</h2>
            <div>
                <Button size={"lg"}>Create Category</Button>
            </div>
        </div>
    )
}

export default Category;
