import React, {useState, useEffect} from "react";
import AuthService from "../../services/auth.service";
import axios from "axios";
import {Button, Card, FormGroup, Table, Form, Label, Input, Container} from "reactstrap";
import {Stack} from "react-bootstrap";
import "./Category.css";
import ProfileButton from "../Profile/ProfileButton";
import CategoryCard from "./CategoryCard";
import CreateCategoryModal from "./CreateCategoryModal";
import UpdateCategoryModal from "./UpdateCategoryModal";

const Category = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [emptyCategory, setEmptyCategory] = useState({categoryName: '', categoryBudget: ''});
    const [allCategories, setAllCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState({categoryName: '', categoryBudget: ''});
    const [selectedCategoryName, setSelectedCategoryName] = useState("");
    const [selectedCategoryBudget, setSelectedCategoryBudget] = useState("");
    const [budget, setBudget] = useState('');
    const [isCreate, setIsCreate] = useState(true);
    const [createModal, setCreateModal] = useState(false);
    const [updateModal, setUpdateModal]  = useState(false);

    const handleClose = () => setCreateModal(false);

    useEffect(() => {
        getAllCategories();
    },[]);

    const getAllCategories = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        const budget = AuthService.getTotalBudget();
        setUsername(username);
        setToken(token);
        setBudget(budget);

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/category`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllCategories(response.data);
        }).catch(error => {
            console.log(error.message);
        })
    }

    const deleteCategory = (name) => {
        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }
        const url = `/${username}/category/${name}`;

        axios.delete(url, requestHeader).then(response => {
            console.log(response.data);
            window.location.reload();
        }).catch(error => {
            console.log(error.message);
        })

    }

    const onUpdateOrCreate = (event) => {
        event.preventDefault();
        const value = event.target.value;
        const name = event.target.name;
        let categoryChange = {...selectedCategory};
        categoryChange[name] = value;
        setSelectedCategory(categoryChange);
    }

    const updateCategory = (event) => {
        event.preventDefault();
        console.log("Updating category....");
        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }


        const catName = selectedCategory.categoryName;
        const catBudget = selectedCategory.categoryBudget.toString();

        const url = `/${username}/category/${catName}`;

        const requestBody = {
            "budget": catBudget
        }

        axios.put(url, requestBody, requestHeader).then(response => {
            console.log(response.data);
            window.location.reload();
        }).catch(error => {
            console.log(error.message);
        })
    }

    const clickOnUpdate = (event, category) => {
        event.preventDefault();
        setSelectedCategory(category);
        setIsCreate(false);
    }

    const clickOnCreate = (event) => {
        event.preventDefault();
        setSelectedCategory(emptyCategory);
        setCreateModal(true);
        //TODo: remove setIsCreate
    }

    /*const categoryRows = allCategories?.map(category => {
        return (
            <tr key={category.categoryName}>
                <td  style= { {textAlign: "center"}}>{category.categoryName}</td>
                <td  style= { {textAlign: "center"}}>{category.categoryBudget}</td>
                <td  style= { {textAlign: "center"}}><Button color={"warning"} className={""} size={"sm"}
                            onClick={(event) => clickOnUpdate(event, category)}>Update Category</Button>
                </td>
                <td  style= { {textAlign: "center"}}>
                    <Button color={"danger"} className={""} size={"sm"}
                            onClick={() => deleteCategory(category.categoryName)}>Delete Category</Button>
                </td>
            </tr>
        )
    })*/

    const openUpdateCategoryModal = (name, budget) => {

        setSelectedCategoryName(name);
        setSelectedCategoryBudget(budget);
        console.log(selectedCategoryName)
        console.log(selectedCategoryBudget)
        setUpdateModal(true);
    }

    const categoryCards = allCategories?.map(category => {
        return (
            <div>

               {CategoryCard(category.categoryName, category.categoryBudget, budget,
                   () => openUpdateCategoryModal(category.categoryName, category.categoryBudget))}
            </div>
        )
    })

    const totalCatBudget = allCategories?.reduce( (acc, obj) => {
        return acc + obj.categoryBudget
    }, 0);

    const totalCatCard = () => {
        return (
            <div>
                {CategoryCard("All Categories", totalCatBudget, budget)}
            </div>
        )
    }


    let specificCategory = (category) => {
        if(isCreate) {
            return (
               <Card>
                   <Form>
                       <FormGroup>
                           <Label htmlFor={"categoryName"}>Category Name</Label>
                          <Input type={"text"} name={"categoryName"} id={"categoryName"}
                                  defaultValue={category.categoryName} required={true} onChange={onUpdateOrCreate} />
                       </FormGroup>
                       <FormGroup>
                           <Label htmlFor={"categoryBudget"}>Category Budget</Label>
                           <Input type={"number"} name={"categoryBudget"} id={"categoryBudget"}
                                  defaultValue={category.categoryBudget} required={true} onChange={onUpdateOrCreate} />
                       </FormGroup>
                       <Button>Create Category</Button>
                   </Form>
               </Card>
            )
        } else {
            return (
                <Card>
                    <Form onSubmit={updateCategory}>
                        <FormGroup>
                            <Label htmlFor={"categoryName"}>Category Name</Label>
                            <Input type={"text"} name={"categoryName"} id={"categoryName"}
                                   value={category.categoryName} readOnly={true} onChange={onUpdateOrCreate} />
                        </FormGroup>
                        <FormGroup>
                            <Label htmlFor={"categoryBudget"}>Category Budget</Label>
                            <Input type={"number"} name={"categoryBudget"} id={"categoryBudget"}
                                   defaultValue={category.categoryBudget} required={true} onChange={onUpdateOrCreate} />
                        </FormGroup>
                        <Button>Update Category</Button>
                    </Form>
                </Card>
            )
        }
    }

    return (
        <Container className={"pt-4"}>
            <Stack direction={"horizontal"} gap={2} className={"mb-4"}>
                <Stack direction={"vertical"}>
                    <h2 className={"me-auto"}>Categories</h2>
                    <h6>Hi {username}, Below is an overview of your categories.</h6>
                </Stack>
                <div className={"buttons"}>
                    <div className={"manage-buttons"}>
                        <ProfileButton />
                    </div>
                    <div className={"manage-buttons"}>
                        <Button color={"info"} onClick={() => setCreateModal(true)}>Create Category</Button>
                    </div>

                </div>
            </Stack>
            <div style={{display:"grid", gridTemplateColumns: "repeat(auto-fill, minmax(500px,50%))", gap: "1rem",
                alignItems: "flex-start"}}>
                {categoryCards}
                {totalCatCard()}
            </div>
            {/*<div className={"categoryTable"}>
                <Table hover={true} bordered={true}>
                    <thead>
                    <tr>
                        <th style={{width: "25%", textAlign: "center"}}>Category Name</th>
                        <th style= {{width: "25%", textAlign: "center"}}>Budget</th>
                        <th style= { {textAlign: "center"}} colSpan={2}>Action</th>
                    </tr>
                    </thead>
                    <tbody>
                        {categoryRows}
                    </tbody>
                </Table>
            </div>*/}
            {/*<div>
                {specificCategory(selectedCategory)}
            </div>*/}
            {CreateCategoryModal(createModal, () => setCreateModal(false))}
            {UpdateCategoryModal(updateModal, ()=> setUpdateModal(false),
                selectedCategoryName, selectedCategoryBudget)}
        </Container>
    )
}

export default Category;
