import React from "react";
import {Button, Form, FormControl, FormGroup, FormLabel, Modal} from "react-bootstrap";
import {useRef} from "react";
import axios from "axios";
import AuthService from "../../services/auth.service";
import {useNavigate} from "react-router-dom";

const CreateCategoryModal = (show, handleClose) => {
    const nameRef = useRef();
    const budgetRef = useRef();
    const navigate = useNavigate;

    const createCategory = (event) => {
        event.preventDefault();
        const catName = nameRef.current.valueOf();
        const catBudget = budgetRef.current.valueOf();
        const username = AuthService.getCurrentUser();
        const token = AuthService.getToken();

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/category`;

        const requestBody = {
            "categoryName": catName,
            "categoryBudget": catBudget
        }

        axios.post(url, requestBody, requestHeader).then(response => {
            console.log(response.data);
            navigate("/categories")
        }).catch(error => {
            console.log(error.message);
        })
    }


    return (
        <div>
            <Modal show onHide={handleClose}>
                <Form onSubmit={createCategory}>
                    <Modal.Header closeButton>
                        <Modal.Title>New Category</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <FormGroup className={"mb-3"} controlId={"name"}>
                            <FormLabel>Category Name</FormLabel>
                            <FormControl itemRef={nameRef} type={"text"} required />
                        </FormGroup>
                        <FormGroup className={"mb-3"} controlId={"budget"}>
                            <FormLabel>Category Budget</FormLabel>
                            <FormControl itemRef={budgetRef} type={"number"} aria-valuemin={0} required />
                        </FormGroup>
                        <div className={"d-flex justify-content-end"}>
                            <Button variant={"primary"}>Create Category</Button>
                        </div>
                    </Modal.Body>

                </Form>

            </Modal>
        </div>
    )
}

export default CreateCategoryModal;
