import React, {useState, useEffect} from "react";
import AuthService from "../../services/auth.service";
import axios from "axios";
import {Container} from "react-bootstrap";
import ExpenseCard from "./ExpenseCard";
import CategoryCard from "../Category/CategoryCard";

const Expense = (props) => {
    const [username, setUsername] = useState('');
    const [token, setToken] = useState('');
    const [allExpenses, setAllExpenses] = useState([]);
    const [budget, setBudget] = useState('');
    const [allCategories, setAllCategories] = useState([]);
    const [expenseGroupByCategory, setExpenseGroupByCategory] = useState({});
    const [loading, isLoading] = useState(true);

    useEffect(() => {
        getAllExpenses();
        getAllCategories();
    },[]);

    const getAllExpenses = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();
        const budget = AuthService.getTotalBudget();
        setUsername(username);
        setToken(token);
        setBudget(budget);

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/expense`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllExpenses(response.data);
            return response.data
        }).catch(error => {
            console.log(error.message);
        }).then(response => response.reduce((acc,d) => {
            if(Object.keys(acc).includes(d.categoryName)) return acc;
            acc[d.categoryName] = response.filter(name => name.categoryName === d.categoryName);
            return acc;
        }, {})).then(result => setExpenseGroupByCategory(result));

    }

    const getAllCategories = () => {
        const token = AuthService.getToken();
        const username = AuthService.getCurrentUser();

        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }

        const url = `/${username}/category`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            setAllCategories(response.data);
            return response.data;
        }).catch(error => {
            console.log(error.message);
        })
    }

    /*const getSpecificBudget = (catName) => {
        const requestHeader = {
            headers : {
                "Content-Type": "application/json",
                "Authorization": "Bearer " + token
            }
        }
        const url = `/${username}/category/${catName}`;

        axios.get(url, requestHeader).then(response => {
            console.log(response.data);
            return response.data.categoryBudget;
        }).catch(error => {
            console.log(error.message);
        })

    }*/

    const eCards =  Object.keys(expenseGroupByCategory).map(key => {
        const catName = key;
        const catTotalBudget = allCategories.filter(cat =>  {
            return cat.categoryName == catName
        });
        const expenses = expenseGroupByCategory[key].reduce((acc,obj) => {
            return acc + obj.amount
        }, 0);

        console.log(catTotalBudget)
        return (
            <div>
                {ExpenseCard(catName,expenses,catTotalBudget,
                    (event) => event.preventDefault(), (event) => event.preventDefault() )}
            </div>
        )

        /*Promise.all([key, catTotalBudget, expenses]).then(value => {
            console.log(value)
            return (
                <div>
                    {ExpenseCard(value[0],value[2], value[1],
                        (event) => event.preventDefault(), (event) => event.preventDefault() )}
                </div>
            )
        })*/


    })

    //const NewComponent = expenseCards();

    return (
        <Container className={"pt-4"}>

            <div style={{display:"grid", gridTemplateColumns: "repeat(auto-fill, minmax(500px,50%))", gap: "1rem",
                alignItems: "flex-start"}}>
                {eCards}
            </div>
        </Container>
    )
}

export default Expense;
