import {Button, Card, ProgressBar, Stack} from "react-bootstrap";
import {currencyFormatter} from "../utils/utils";
import React from "react";

const getProgressBarVariant = (amount, max) => {
    const ratio = amount/max;
    if(ratio < 0.5)
        return "primary"
    if(ratio < 0.75)
        return "warning"
    else
        return "danger"
}

class CategoryCard extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <Card>
                <Card.Body>
                    <Card.Title className={"d-flex justify-content-between align-items-baseline fw-normal mb-3"}>
                        <div className={"me-2"}>{props.categoryName}</div>
                        <div className={"d-flex align-items-baseline"}>{currencyFormatter.format(props.categoryBudget)}
                            <span className={"text-muted fs-6 ms-1"}> / {currencyFormatter.format(props.totalBudget)} </span></div>
                    </Card.Title>
                    <ProgressBar className={"rounder-pill"} variant={getProgressBarVariant(props.categoryBudget, props.totalBudget)}
                                 min={0} now={props.categoryBudget} max={props.totalBudget}/>
                    <Stack direction={"horizontal"} gap={2} className={"mt-4"}>
                        <Button variant={"primary"} className={"ms-auto"}>Update Category</Button>
                        <Button variant={"outline-danger"}>Delete Category</Button>
                    </Stack>
                </Card.Body>
            </Card>
        )
    }


}


export default CategoryCard;
