import {currencyFormatter, dateFormatter} from "../utils/utils";
import {Modal, Stack} from "react-bootstrap";


const ViewExpenseModal = (show, handleClose, expenses, categoryName) => {

    const generateRows = expenses?.map(exp => {
        console.log(exp);
        console.log(new Date(exp.expenseDate).toISOString());
        return (
            <Stack direction={"horizontal"} gap={2} key={exp.expenseId}>
                <div className={"me-auto fs-5"}>{exp.description}</div>
                <div className="fs-5">{currencyFormatter.format(exp.amount)}</div>
                <div className="fs-5">{new Date(exp.expenseDate).toISOString().substring(0,10)}</div>
            </Stack>
        )
    })

    /*const generateRows = () => {
        return (
            {expenses?.map(exp => {
                <Stack direction={"horizontal"} gap={2} key={exp.expenseId}>
                    <div className={"me-auto fs-4"}>{exp.description}</div>
                    <div className="fs-5">{currencyFormatter.format(exp.amount)}</div>
                </Stack>
            })}
        )
    }*/

    return (
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>
                    <Stack direction={"horizontal"} gap={2}>
                        <div>Expenses: {categoryName}</div>

                    </Stack>
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Stack direction={"vertical"} gap={2}>
                    {generateRows}
                </Stack>
            </Modal.Body>
        </Modal>
    )
}

export default ViewExpenseModal;
